import React, { useState, useEffect } from "react";
import { Button, StyleSheet, Text, View } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";

import Alarm from "./Alarm";

const ExistingAlarm = ({navigation}) => {
  const [data, setData] = useState(null);
  const [deletingKey, setDeletingKey] = useState(null)

  useEffect(() => {
    const getData = async () => {
      let keys = [];
      try {
        keys = await AsyncStorage.getAllKeys();
        setData(keys);
      } catch (e) {}
    };
    getData();

    removeValue(deletingKey)
  }, [deletingKey]);

  const removeValue = async (key) => {
    try {
      await AsyncStorage.removeItem(key)
    } catch(e) {
      // remove error
    }

    console.log('Done.')
  }

  return (
    <View>
      {data && (
        <View>
          {data.map((alarm, index) => (
            <Alarm key={index} alarm={alarm} navigation={navigation} deletingKey={deletingKey} setDeletingKey={setDeletingKey} />
          ))}
        </View>
      )}
    </View>
  );
};

export default ExistingAlarm;
