import React, { useState, useEffect, useContext } from "react";
import { StyleSheet, Text, TouchableOpacity } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";

import {EditAlarmContext} from '../context/EditAlarmContextProvider'

const Alarm = ({ navigation, alarm }) => {
  const [data, setData] = useState({});

  const {setEditingAlarmData} = useContext(EditAlarmContext)

  const getMyObject = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem(alarm);
      jsonValue != null ? JSON.parse(jsonValue) : null;
      setData(JSON.parse(jsonValue));
    } catch (e) {
      console.log(e);
      // read error
    }
  };

  useEffect(() => {
    getMyObject();
  }, []);

  return (
    <TouchableOpacity style={styles.border} onPress={() => {setEditingAlarmData(data); navigation.navigate("ExistingAlarm") }}>
      <Text style={styles.text}>Coin Pair: {data.coinPair}</Text>
      <Text style={styles.text}>Side: {data.moreThan}</Text>
      <Text style={styles.text}>Price: {data.price}</Text>
      <Text style={styles.text}>Sound: {data.alarmSound}</Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  border: {
    borderWidth: 1,
  },
  text: {
    color: "red",
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
    backgroundColor: "black",
  },
});



export default Alarm;
